import { useEffect, useState } from "react";
import { getGifts } from "../helpers/getGifs";

export const useFetchCards = (keyword) => {
  const [obj, setState] = useState({
    data: [],
    loading: true,
  });

  useEffect(() => {
    getGifts(keyword).then((imgs) => {
      setState({
        data: imgs,
        loading: false,
      });
    });
  }, [keyword]);

  return obj;
};
