import React, { useState } from "react";
import _ from "lodash";

export const InputSearch = ({ setKeyword }) => {
  const [inputValue, setInputValue] = useState("");

  const handleInputChamge = (e) => {
    setInputValue(e.target.value);
    setKeyword(e.target.value);
    console.log(inputValue);
  };

  return <div className="mt-5">
      <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" onChange={_.debounce(handleInputChamge, 500)} />
    </div>;
};
