import { useEffect, useState } from "react"
import { getGifts } from "../helpers/getGifs";


export const useFetchGifs = ( category ) => {
    const [obj, setState] = useState( {
        data: [],
        loading: true
    });

    useEffect( () => {
        getGifts( category )
            .then( imgs => {
                setState({
                    data: imgs,
                    loading: false
                });
            } )
    }, [ category ] )

    return obj;
}
