    // }

    return (
        <div className="container mx-auto">
            <h2 className="text-black-500 text-3xl mt-5 font-rale font-black">Gif Expert App</h2>
            <hr />
            <AddCategory setCategories={ setCategories } />

            <ol type="1">
                { 
                    categories.map( category =>
                        <GifGrid 
                            key={ category }
                            category={ category } 
                        />
                    ) 
                    // categories.map( category => {
                    //     return <li key={ category }> { category } </li>
                    // }) 
                }
            </ol>
        </div>
    )
}
