export const getFetchCards = async ( category )  => {

    if(category.trim().length > 0 ) {
        const url = `https://api.giphy.com/v1/gifs/search?q=${ encodeURI( category ) }&limit=10&api_key=N5HCK1RKEn0gVCa5b37BDsfS5PsVWDXl`;
        const resp = await fetch( url );
        const { data } = await resp.json();

        const cards = data.map ( img => {
            return {
                id: img.id,
                title: img.title,
                url: img.images?.downsized_medium.url
            }
        })

        return cards;
    } else {
        return [];
    }
}
