import React from 'react'
import { useFetchCards } from '../../hooks/useFetchCards';
import { Card } from './Card';

// import { GifGriditem } from './GifGriditem';

// export const GridCards = ( { category } ) => {
export const GridCards = ( { keyword } ) => {

    const { data, loading } = useFetchCards( keyword );    

    return (
        <>
            <h3> { keyword } </h3>

            { loading && <p>Loading</p> }

            <div className="my-5 flex flex-wrap -mx-2">
                { 
                    data.map( card => (
                        <Card 
                            key= { card.id }
                            { ...card }
                        />
                    ))
                }        
            </div>
        </>
    )
}
