import React, { useState } from "react";

export const InputSearch = ({ setKeyword }) => {
  const [inputValue, setInputValue] = useState('');

  const handleInputChamge = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (inputValue.trim().length > 2) {
        setKeyword(inputValue);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* <h1>{inputValue}</h1> */}
      <input type="text" value={inputValue} onChange={handleInputChamge} />

      {/* <button class="bg-red-500 hover:bg-red-700 text-white px-3 py-3 font-bold rounded w-500">
        Search
      </button> */}
    </form>
  );
};
