import { useEffect, useState } from "react";
import { getFetchCards } from "../services/getFetchCards";

export const useFetchCards = (keyword) => {

  const [obj, setState] = useState({
    data: [],
    loading: true,
  });

  useEffect(() => {
    getFetchCards(keyword).then((cards) => {
      setTimeout( () => {
        setState({
          data: cards,
          loading: false,
        });
      }, 3000);
    });
  }, [keyword]);

  return obj;
};
